import React, { useState, useEffect, useRef } from "react";

export default function EditPermutationUI() {
  const [drafts, setDrafts] = useState(["A"]);
  const [selectedDraft, setSelectedDraft] = useState("A");
  const [editText, setEditText] = useState("");
  const [editType, setEditType] = useState("add");
  const [conditionParts, setConditionParts] = useState([]);
  const [highlighted, setHighlighted] = useState([]);
  const [history, setHistory] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const [graphEdges, setGraphEdges] = useState([{ from: null, to: "A" }]);
  const draftBoxRef = useRef();

  useEffect(() => {
    const handleKey = (e) => {
      if (e.ctrlKey && e.key === "z") undo();
      if (e.ctrlKey && e.key === "y") redo();
    };
    window.addEventListener("keydown", handleKey);
    return () => window.removeEventListener("keydown", handleKey);
  }, [history, redoStack]);

  function saveHistory(newDrafts, newEdges) {
    setHistory([...history, drafts]);
    setRedoStack([]);
    setDrafts(newDrafts);
    setGraphEdges([...graphEdges, ...newEdges]);
  }

  function undo() {
    if (history.length > 0) {
      const prev = history[history.length - 1];
      setRedoStack([drafts, ...redoStack]);
      setHistory(history.slice(0, -1));
      setDrafts(prev);
    }
  }

  function redo() {
    if (redoStack.length > 0) {
      const next = redoStack[0];
      setHistory([...history, drafts]);
      setRedoStack(redoStack.slice(1));
      setDrafts(next);
    }
  }

  function applyEdit() {
    const newDrafts = new Set(drafts);
    const edges = [];

    for (let draft of drafts) {
      const meetsCondition =
        conditionParts.length === 0 ||
        conditionParts.every((p) => draft.includes(p));

      if (!meetsCondition) continue;

      let result = draft;

      if (editType === "add") {
        result += editText;
      } else if (editType === "remove" && draft.includes(editText)) {
        result = draft.replace(editText, "");
      } else {
        continue;
      }

      if (!newDrafts.has(result)) {
        newDrafts.add(result);
        edges.push({ from: draft, to: result });
      }
    }

    saveHistory([...newDrafts], edges);
    setEditText("");
    setConditionParts([]);
    setHighlighted([]);
  }

  function handleSelect() {
    const sel = window.getSelection();
    if (sel && sel.toString().length > 0) {
      const selected = sel.toString();
      setConditionParts((prev) =>
        window.event.ctrlKey || window.event.metaKey
          ? [...prev, selected]
          : [selected]
      );
      setHighlighted((prev) =>
        window.event.ctrlKey || window.event.metaKey
          ? [...prev, selected]
          : [selected]
      );
      sel.removeAllRanges();
    }
  }

  function renderWithHighlights(draft) {
    if (highlighted.length === 0) return draft;

    let segments = [draft];
    for (let frag of highlighted) {
      segments = segments.flatMap((seg) =>
        typeof seg === "string" && seg.includes(frag)
          ? seg.split(frag).flatMap((part, i, arr) =>
              i < arr.length - 1
                ? [part, <mark key={part + i}>{frag}</mark>]
                : [part]
            )
          : [seg]
      );
    }
    return segments;
  }

  return (
    <div className="p-4 space-y-6 text-gray-800">
      <h1 className="text-2xl font-bold">Edit Permutation UI</h1>

      <div>
        <h2 className="font-semibold">All Drafts:</h2>
        <ul className="flex flex-wrap gap-2">
          {drafts.map((d, i) => (
            <li
              key={i}
              onClick={() => {
                setSelectedDraft(d);
                setHighlighted([]);
                setConditionParts([]);
              }}
              className={`px-2 py-1 rounded cursor-pointer ${
                d === selectedDraft ? "bg-blue-200" : "bg-gray-100"
              }`}
            >
              {d}
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h2 className="font-semibold">Selected Draft:</h2>
        <div
          ref={draftBoxRef}
          onMouseUp={handleSelect}
          className="p-2 border rounded bg-white whitespace-pre-wrap min-h-[60px] cursor-text"
        >
          {renderWithHighlights(selectedDraft)}
        </div>
      </div>

      <div className="space-y-2">
        <select
          className="border p-2 rounded"
          value={editType}
          onChange={(e) => setEditType(e.target.value)}
        >
          <option value="add">Add Text</option>
          <option value="remove">Remove Text</option>
        </select>

        <input
          className="border p-2 rounded w-full"
          placeholder={editType === "add" ? "Text to add" : "Text to remove"}
          value={editText}
          onChange={(e) => setEditText(e.target.value)}
        />

        <div className="text-sm text-gray-600">
          Conditions:{" "}
          {conditionParts.length > 0 ? conditionParts.join(", ") : "(none)"}
        </div>

        <button
          onClick={applyEdit}
          className="bg-blue-600 text-white px-4 py-2 rounded"
          disabled={!editText}
        >
          Submit Edit
        </button>
        <button onClick={undo} className="ml-2 px-4 py-2 bg-gray-200 rounded">
          Undo (Ctrl+Z)
        </button>
        <button onClick={redo} className="ml-2 px-4 py-2 bg-gray-200 rounded">
          Redo (Ctrl+Y)
        </button>
      </div>

      <div>
        <h2 className="font-semibold mt-6">Version Graph:</h2>
        <ul className="space-y-1">
          {graphEdges.map((edge, i) =>
            edge.from ? (
              <li key={i}>
                <span
                  className="text-blue-600 underline cursor-pointer"
                  onClick={() => setSelectedDraft(edge.from)}
                >
                  {edge.from}
                </span>{" "}
                →{" "}
                <span
                  className="text-blue-600 underline cursor-pointer"
                  onClick={() => setSelectedDraft(edge.to)}
                >
                  {edge.to}
                </span>
              </li>
            ) : (
              <li key={i}>Initial: {edge.to}</li>
            )
          )}
        </ul>
      </div>
    </div>
  );
}
